import React, { Component } from 'react';
import CoursForm from './CourseForm'
import CoursList from './CourseList'
class App2 extends Component {
    constructor(){
    super()
    this.state={courses
       : [{id:1,name:'html'},
            {id:2,name:'java'},
            {id:3,name:'css'}

        ]
    }

};
	render() {
        const courselist=courses.map((cor)=>{
            <CoursList key={cor.id} 
            name={cor.name}
            />
        })
		return (
			<div>
                <CoursForm/>
                <ul>
                {courselist}</ul>
			
            	
			</div>
		);
	}
}
export default App2;
