import React, { Component } from 'react';
import TeamMember from './TeamMember';

class Team extends Component {
	constructor() {
		super();
		this.state = {
			memberInfo: [
				{
					id: 1,
					img: '/img/1.jpg',
					name: 'Dominic Purcell',
					position: '11111',
					phone: '33335333',
					email: '999',
					website: '99'
				},

				{
					id: 3,
					img: '/img/2.jpg',
					name: 'Wentworth Miller',
					position: '6666',
					phone: '55555556214',
					email: '8jbkj'
				},

				{
					id: 2,
					img: '/img/3.jpg',
					name: 'Robin Tunney',
					position: 'vgc15',
					phone: '66666',
					email: 'kljbgdxk22',
					website: '5213mnn'
				},

				{
					id: 4,
					img: '/img/4.jpg',
					name: 'Peter Stormare',
					position: 'zdfgfhjgj',
					phone: '669999',
					email: 'dttyuyfiu47',
					website: 'sfhfdkhkh'
				},

				{
					id: 5,
					img: '/img/5.jpg',
					name: 'Sarah Wayne Callies',
					position: 'dy',
					phone: '55555',
					email: 'ohgcfxgvj',
					website: '6413132/.,kjhgf'
				},

				{
					id: 6,
					img: '/img/6.jpg',
					name: 'Amaury Nolasco',
					position: 'hjhjjty',
					phone: '55555',
					email: '84545132loji'
				}
			]
		};
	}
	//حطينا xمشان ازا عنا اكثر من مصفوفة يكرلر عليها نفس العملية
	mappig(x) {
		const members = x.map((member) => (
			<TeamMember
				key={member.id}
				img={member.img}
				name={member.name}
				position={member.position}
				phone={member.phone}
				email={member.email}
				website={member.website}
			/>
		));
		return members;
	}
	render() {
		return <div className="row">{this.mappig(this.state.memberInfo)}</div>;
	}
}
export default Team;
