import React, { Component } from 'react';
import ServiesMember from './ServiesMember';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCoffee, faChartLine } from '@fortawesome/fontawesome-free-solid';

class Servies extends Component {
	constructor() {
		super();

		this.state = {
			res: [
				{
					id: 10,
					icon: { faCoffee }
				},
				{
					id: 11,
					icon: { faChartLine }
				}
			],
			/*<FontAwesomeIcon icon={faCoffee} />,
			<FontAwesomeIcon icon={faChartLine} />,*/
			serviesInfo: [
				{
					id: 1,

					text: 'lorem ipsum dolor sitbnconsectetur adipiscing elitlmkamet'
				},

				{
					id: 3,

					text: 'lorem ipsum dolor sitbnconsectetur adipiscing elitlmkamet'
				},

				{
					id: 2,

					text: 'lorem ipsum dolor sitbnconsectetur adipiscing elitlmkamet'
				}
			]
		};
	}
	//حطينا xمشان ازا عنا اكثر من مصفوفة يكرلر عليها نفس العملية
	/*mappig(y) {
		const mem = y.map((sr) => <ServiesMember key={sr.id} text={sr.text} />);
		return mem;
	}*/
	mappig(x) {
		const ded = x.map((dad) => <FontAwesomeIcon icon={dad.icon} />);
		return ded;
	}
	render() {
		return <div className="row">{this.mappig(this.state.res)}</div>;
	}
}
export default Servies;
