import React from 'react';

function TodoItem0(props) {
	const { it } = props;
	const { deletItem } = props;
	//or const {it, deletItem}=props;

	const ListItems = it.map((item) => {
		return (
			<div key={item.id}>
				<span> {item.name}</span>
				<span> {item.age}</span>
				<span onClick={deletItem(item.id)}> &times; </span>
			</div>
		);
	});
	return (
		<div className="ListItems">
			<div>
				<span> Name</span>
				<span> Age</span>
				<span>Action</span>
			</div>

			{ListItems}
		</div>
	);
}
export default TodoItem0;
