import React, { Component } from 'react';
import TodoItem0 from './Todoitem0';
import AddItem from './Additem';
class TodoItem extends Component {
	state = {
		items: [ { id: 1, name: 'Sedra', age: 29 }, { id: 2, name: 'Ali', age: 28 }, { id: 3, name: 'Sali', age: 26 } ]
	};
	deletItem = (id) => {
		let ite = this.state.items;
		//حفظنا كل العناصر ضمن متحول اسمه ite
		let i = ite.findIndex((x) => x.id === id);
		//xرح يعدي على كل عناصر المصفوفة
		//ازا الاي دي يلي انت بعتا تساوي الاي دي .اكس احذف هذا العنصر من خلال سبلاسي
		ite.splice(i, 1); //احذف عنصر واحد فقط
		this.setState({ items: ite });
	};

	/*addItem = (item) => {
		item.id = Math.random(); //لحتا يضيف ايدي لكل عنصر ويعطي رقم من صفر لعشرة
		let nitem = this.state.items;
		nitem.push(item);
		this.setState({ items: nitem });addItem={this.addItem}
	};*/
	render() {
		return (
			<div>
				<TodoItem0 it={this.state.items} deletItem={this.deletItem} />
				<AddItem />
			</div>
		);
	}
}
export default TodoItem;
