import React, { Component } from 'react';
import CoursForm from './CourseForm'
import CoursList from './CoursList'
class App2 extends Component {
    constructor(){
    super()
    this.state={
        courses: 
        [{id:1,name:'html'},
            {id:2,name:'java'},
            {id:3,name:'css'}

        ],
        curent:''
    }

};
updateCourse=(e)=>{
this.setState({curent: e.target.value})


}
addCourse=(e)=>{
    e.preventDefault();
    let cur=this.state.curent;
    let cors=this.state.courses;
    cors.push({name:cur})
this.setState({
    courses:cors
    ,curent:''
})
}

	render() {
     this.courselist= this.state.courses.map((corr)=>{
            return <CoursList 
            key={corr.id} 
            name={corr.name}
            />
        })
		return (
			<div>
                <CoursForm  updateCourse={this.updateCourse} addCourse={this.addCourse} current={this.state.curent}/>
                <ul>
                {this.courselist}</ul>
			
            	
			</div>
		);
	}
}
export default App2;
