import React, { Component } from 'react';

class AddItem extends Component {
	state = {
		name: '',
		age: ''
	};
	handleChange = (e) => {
		this.setState({
			[e.target.id]: e.target.value // لما بنكتب جوات الالانبوت طالعلي القيمة حطينا ايدي لحتا نعرف انونحنا موجودين وين يعني ضمن النيم ولا ضمن العمر
		});
	};
	handleSubmit = (e) => {
		e.preventDefault();
		if (e.target.name.value === ' ') {
			//يعني ازا ماكتب المستخدم شي
			return false;
		} else {
			this.props.addItem(this.state);
			//ندهنا للاد ايتم وضفنالها العناصر يلي عنا يلي موجودين بالستيت
			//لما بنكبس على السبميت رح يعرض الاد ايتم
			this.setState({
				//مشان بعد مانكتب اي عنصر وضيفو يرجع يفضي الانبوت ومشات تكمل هلخطوة معنا رح نضيف الفاليو عند كل انبوت
				name: ' ',
				age: ' '
			});
		}
	};

	render() {
		return (
			<div>
				<form onSubmit={this.handleSubmit}>
					<input
						type="text"
						placeholder="Enter name "
						id="name"
						onChange={this.handleChange}
						value={this.state.name}
					/>
					<input
						type="number"
						placeholder="Enter age "
						id="age"
						onChange={this.handleChange}
						value={this.state.age}
					/>
					<input type="submit" value="add" />
				</form>
			</div>
		);
	}
}
export default AddItem;
