import React, { Component } from 'react';

class TeamMember extends Component {
	render() {
		return (
			<div className="col-md-4 col-sm-6">
				<div classNam="card">
					<div className="card-header">
						<img style={{ maxWidth: '100%' }} src={this.props.img} />
					</div>
					<div className="card-body">
						<h2>{` name: ${this.props.info.name}`}</h2>
						<h5>{`position: ${this.props.info.position}`}</h5>
						<div>{`phone: ${this.props.info.phone}`}</div>
						<div>{`email: ${this.props.info.email}`}</div>
						<div>{`website: ${this.props.info.website}`}</div>
					</div>
				</div>
			</div>
		);
	}
}
export default TeamMember;
