import React, { Component } from 'react';
import CoursForm from './CourseForm'
import CoursList from './CoursList'
class App2 extends Component {
    constructor(){
    super()
    this.state={courses
       : [{id:1,name:'html'},
            {id:2,name:'java'},
            {id:3,name:'css'}

        ]
    }

};
updateCourse=(e)=>{
console.log(e.target.value)


}
addCourse=(e)=>{
    e.preventDefault();
}

	render() {
     this.courselist= this.state.courses.map((corr)=>{
            return <CoursList 
            key={corr.id} 
            name={corr.name}
            />
        })
		return (
			<div>
                <CoursForm  updateCourse={this.updateCourse} addCourse={this.addCourse}/>
                <ul>
                {this.courselist}</ul>
			
            	
			</div>
		);
	}
}
export default App2;
