import React, { Component } from 'react';
import TodoItem0 from './Todoitem0';
import AddItem from './Additem';
class TodoItem extends Component {
	state = {
		items: [ { id: 1, name: 'Sedra', age: 29 }, { id: 2, name: 'Ali', age: 28 }, { id: 3, name: 'Sali', age: 26 } ]
	};
	deletItem = (id) => {
		let del = this.state.items;
		//حفظنا كل العناصر ضمن متحول اسمه del
		let ddel = del.findIndex((x) => x.id === id);
		//xرح يعدي على كل عناصر المصفوفة
		//ازا الاي دي يلي انت بعتا تساوي الاي دي .اكس احذف هذا العنصر من خلال سبلاسي
		del.splice(ddel, 1); //احذف عنصر واحد فقط
		this.setState({ items: del });
	};

	addItem = (item) => {
		item.id = Math.random(); //لحتا يضيف ايدي لكل عنصر ويعطي رقم من صفر لعشرة
		let nitem = this.state.items;
		nitem.push(item);
		this.setState({ items: nitem });
	};
	render() {
		return (
			<div className="container">
				<h1 className="text-center">Todo list</h1>
				<TodoItem0 itm={this.state.items} deletItem={this.deletItem} />
				<AddItem addItem={this.addItem} />
			</div>
		);
	}
}
export default TodoItem;
