import React,{Component} from 'react';
import axios from 'axios'
import {PortfolioSection,PortfolioTitle,Span,PortfolioList,PortfolioItem,Image,Img,Overlay,OverlaySpan} from './style'


class Portfolio extends Component{
	state={
		port:[]
	}
	componentDidMount(){
		axios.get('js/data.json').then(res => {this.setState({port: res.data.portfolio})})
	}
	render(){	const {port}=this.state;
	const ImgList =port.map(  (ImgItem) =>{
		return(  
                <Image key={ImgItem.id}>
                    <Img src={ImgItem.image} alt=""/>
                    <Overlay>
                        <OverlaySpan>
                            Show Image
                        </OverlaySpan>
                    </Overlay>
                </Image>
           	
		)})
return(
<PortfolioSection>
	<PortfolioTitle><Span>My</Span> Portfolio</PortfolioTitle>
	<PortfolioList>
		<PortfolioItem active>All</PortfolioItem>
		<PortfolioItem>HTML</PortfolioItem>
		<PortfolioItem>Photoshop</PortfolioItem>
		<PortfolioItem>Wordpress</PortfolioItem>
		<PortfolioItem>Mobile</PortfolioItem>
	</PortfolioList>
	

		{ImgList}     
	
            
			</PortfolioSection>

)

	}


};
export default Portfolio;
