import React, { Component } from 'react';
import CoursForm from './CourseForm'
import CoursList from './CoursList'
class App2 extends Component {
    constructor(){
    super()
    this.state={
        courses: 
        [{name:'html'},
            {name:'java'},
            {name:'css'}

        ],
        curent:''
    }

};
updateCourse=(e)=>{
this.setState({curent: e.target.value})


}
addCourse=(e)=>{
    e.preventDefault();
    let cur=this.state.curent;
    let cors=this.state.courses;
    cors.push({name:cur})
this.setState({
    courses:cors
    ,curent:''
})
}
deleteCorse=(index)=>{
    let curs=this.state.courses;
    curs.splice(index,1);
    this.setState({courses:curs})
}
//لازم يعرف على اي عنصر بدنا نعدل لهيك حطينا اندكس وحطينا فالبو احتا ياخد القيمة الجديدة وحطينا كل الكورسات بالمتحول كورسس
//بعدين هات الكورس حسب الاندكس تبعها
//والكورس خليها بالفاليو الجديد
editCourse=(index,value)=>{
    let courses =this.state.courses;
    let course=courses[index];
    course['name']=value;
    this.setState({courses})
}
	render() {
     this.courselist= this.state.courses.map((corr,index)=>{
            return <CoursList 
            key={index} 
            index={index}
            name={corr.name}
            delete ={this.deleteCorse}
            edit={this.editCourse}/>
        })
		return (
			<div>
                <h1>Add Courses</h1>
                <CoursForm  updateCourse={this.updateCourse} addCourse={this.addCourse} current={this.state.curent}/>
                <ul>
                {this.courselist}</ul>
			
            	
			</div>
		);
	}
}
export default App2;
