import React, { Component } from 'react';
import { Fragment } from 'react';

class CoursList extends Component {
	
	state={isEdit:false};
	renderCourse=()=>{return (
		
		<li><span>
		   {this.props.name}</span>
		   <button onClick={()=>{this.toggl()}}>Edit Course</button>
		
		   <button onClick={()=>{this.props.delete(this.props.index)}}>Delet</button>
			</li>	
		
		/*حطينا ارو فنكشن لحتا يطبق لبوتون بدونها مابيعمل كليك */
	)}
	renderUpdate=()=>{
	return(
		<form onSubmit={this.updateItem}>
			<input type="text" ref={(v)=>{this.input=v}} defaultValue={this.props.name} />
			<button >Update Course</button>
		</form>
	)	
	}
	toggl=()=>{
		let {isEdit}=this.state//جيب الادت من ذس اديت
		this.setState({ isEdit:!isEdit})//جيب عكس الادت
	}
	updateItem=(e)=>{
e.preventDefault();
this.props.edit(this.props.index,this.input.value)
this.toggl();
	}
	render() {
		let {isEdit}=this.state;
		return (
			
				<Fragment>
			{ isEdit ? this.renderUpdate() : this.renderCourse()}
              	</Fragment>
			
			/*حطينا ارو فنكشن لحتا يطبق لبوتون بدونها مابيعمل كليك */
		);
	}
}
export default CoursList;
