import React from 'react';

function TodoItem0(props) {
	const { it } = props;

	const { deletItem } = props;
	let length = it.length;
	//or const {it, deletItem}=props;

	const ListItems = length ? (
		it.map((item) => {
			return (
				/*<div key={item.id}>
				<span> {item.name}</span>
				<span> {item.age}</span>

				ازا خلينا بهلشكل رح ينفذ مباشرة 
			 بس نحنا بدنا انو لما نضغط وقتها ينفذ لهيك بتضيف فنكشن جديد 
			<span onClick={deletItem(item.id)}> &times; </span>
			</div>*/
				<div key={item.id}>
					<span className="name"> {item.name}</span>
					<span className="age"> {item.age}</span>
					<span className="action" onClick={() => deletItem(item.id)}>
						{' '}
						&times;{' '}
					</span>
				</div>
			);
		})
	) : (
		<p> Ther is no item to show</p>
	);
	return (
		<div className="ListItems">
			<div>
				<span className="name title"> Name</span>
				<span className="age title"> Age</span>
				<span className="actoin title">Action</span>
			</div>

			{ListItems}
		</div>
	);
}
export default TodoItem0;
